package myfirstpackage;

public class ComparisonOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a = 10;
int b = 20;

System.out.println("is a equal to b "+ (a==b));
System.out.println("is not equal to b "+ (a!=b));
System.out.println("is greater than b "+ (a>b));
System.out.println("is less than to b "+ (a<b));
System.out.println("is greater or equal to b "+ (a>=b));
System.out.println("is less or equal to b "+ (a<=b));
	}

}
